INSERT INTO cmorvar VALUES('mrro-CMIP6_Lmon','mon','land','runoff_flux','kg m-2 s-1','area: mean where land time: mean','area: areacella','Total Runoff','The total run-off (including drainage through the base of the soil model) per unit area leaving the land portion of the grid cell.','longitude latitude time','mrro','real','','','','','','','');
