INSERT INTO cmorvar VALUES('mrso-CMIP6_Lmon','mon','land','mass_content_of_water_in_soil','kg m-2','area: mean where land time: mean','area: areacella','Total Soil Moisture Content','the mass per unit area  (summed over all soil layers) of water in all phases.','longitude latitude time','mrso','real','','','','','','','');
