INSERT INTO cmorvar VALUES('reffclic-MIP_APmonLev','mon','atmos','effective_radius_of_convective_cloud_ice_particles','m','area: time: mean','area: areacella','Hydrometeor Effective Radius of Convective Cloud Ice','This is defined as the in-cloud ratio of the third moment over the second moment of the particle size distribution (obtained by considering only the cloudy portion of the grid cell).','longitude latitude alevel time','reffclic','real','','','','','','','');
