INSERT INTO cmorvar VALUES('reffclwc-MIP_APmonLev','mon','atmos','effective_radius_of_convective_cloud_liquid_water_particles','m','area: time: mean','area: areacella','Convective Cloud Liquid Droplet Effective Radius','Droplets are liquid.  The effective radius is defined as the ratio of the third moment over the second moment of the particle size distribution and the time-mean should be calculated, weighting the individual samples by the cloudy fraction of the grid cell.','longitude latitude alevel time','reffclwc','real','','','','','','','');
