INSERT INTO cmorvar VALUES('smc-MIP_APmonLev','mon','atmos','atmosphere_net_upward_shallow_convective_mass_flux','kg m-2 s-1','area: time: mean','area: areacella','Shallow Convective Mass Flux','The net mass flux represents the difference between the updraft and downdraft components.  For models with a distinct shallow convection scheme, this is calculated as convective mass flux divided by the area of the whole grid cell (not just the area of the cloud).','longitude latitude alevhalf time','smc','real','up','','','','','','');
