INSERT INTO cmorvar VALUES('tnhusd-MIP_APsubhrPtSite','subhrPt','atmos','tendency_of_specific_humidity_due_to_diffusion','s-1','area: point time: point','','Tendency of Specific Humidity Due to Numerical Diffusion','Tendency of specific humidity due to numerical diffusion.This includes any horizontal or vertical numerical moisture diffusion not associated with the parametrized moist physics or the resolved dynamics.  For example, any vertical diffusion which is part of the boundary layer mixing scheme should be excluded, as should any diffusion which is included in the terms from the resolved dynamics.   This term is required to check the closure of the moisture budget.','alevel site time1','tnhusd','real','','','','','','','');
INSERT INTO cmorvar VALUES('tnhusmp-MIP_APsubhrPtSite','subhrPt','atmos','tendency_of_specific_humidity_due_to_model_physics','s-1','area: point time: point','','Tendency of Specific Humidity Due to Model Physics','Tendency of specific humidity due to model physics. This includes sources and sinks from parametrized moist physics (e.g. convection, boundary layer, stratiform condensation/evaporation, etc.) and excludes sources and sinks from resolved dynamics or from horizontal or vertical numerical diffusion not associated with model physics.  For example any diffusive mixing by the boundary layer scheme would be included.','alevel site time1','tnhusmp','real','','','','','','','');
