INSERT INTO cmorvar VALUES('tntd-MIP_APsubhrPtSite','subhrPt','atmos','tendency_of_air_temperature_due_to_diffusion','K s-1','area: point time: point','','Tendency of Air Temperature Due to Numerical Diffusion','This includes any horizontal or vertical numerical temperature diffusion not associated with the parametrized moist physics or the resolved dynamics.  For example, any vertical diffusion which is part of the boundary layer mixing scheme should be excluded, as should any diffusion which is included in the terms from the resolved dynamics.   This term is required to check the closure of the temperature budget.','alevel site time1','tntd','real','','','','','','','');
INSERT INTO cmorvar VALUES('tntmp-MIP_APsubhrPtSite','subhrPt','atmos','tendency_of_air_temperature_due_to_model_physics','K s-1','area: point time: point','','Tendency of Air Temperature Due to Model Physics','Tendency of air temperature due to model physics. This includes sources and sinks from parametrized physics (e.g. radiation, convection, boundary layer, stratiform condensation/evaporation, etc.). It excludes sources and sinks from resolved dynamics and numerical diffusion not associated with parametrized physics.  For example, any vertical diffusion which is part of the boundary layer mixing scheme should be included, while numerical diffusion applied in addition to physics or resolved dynamics should be excluded.  This term is required to check the closure of the heat budget.','alevel site time1','tntmp','real','','','','','','','');
