INSERT INTO cmorvar VALUES('difvmbo-MIP_OPmonClimLev','monC','ocean','ocean_vertical_momentum_diffusivity_due_to_background','m2 s-1','area: mean time: mean within years time: mean over years','area: areacello volume: volcello','Ocean Vertical Momentum Diffusivity Due to Background','Vertical/dianeutral diffusivity applied to momentum due to the background (i.e. caused by a time invariant imposed field which may be either constant over the globe or spatially varying, depending on the ocean model used).','longitude latitude olevel time2','difvmbo','real','','','','','','','');
